  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'happy-path',
	title: 'Happy Path',
	sidebar_label: 'Happy Path'
};
export const metadata = {
  "id": "happy-path",
  "title": "Happy Path",
  "description": "&nbsp; WARNING: Never send funds (tokens or ETH) directly to the escrow smart contract. Those funds will not be recoverable.",
  "source": "@site/docs/happy-path.md",
  "permalink": "/savedao/docs/happy-path",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/happy-path.md",
  "sidebar_label": "Happy Path",
  "sidebar": "howSidebar",
  "previous": {
    "title": "How Does It Work?",
    "permalink": "/savedao/docs/how-does-it-work"
  },
  "next": {
    "title": "In Case of Dispute",
    "permalink": "/savedao/docs/dispute"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <blockquote>
      <p parentName="blockquote">{`⚠️`}{` `}{` `}<strong parentName="p">{`WARNING:`}</strong>{` Never send funds (tokens or ETH) directly to the escrow smart contract. Those funds will not be recoverable.`}</p>
    </blockquote>
    <ol>
      <li parentName="ol">{`Save Campaign lead (i.e., `}<span id='monk'>{`Cleric`}</span>{` or `}<span id='monk'>{`Monk`}</span>{`) `}<a parentName="li" {...{
          "href": "https://xdai.escrow.raidguild.org/"
        }}>{`creates an escrow`}</a>{` for the raid, with the following info:`}<ul parentName="li">
          <li parentName="ul"><strong parentName="li">{`Save Campaign multisig address`}</strong>{` (xDAI Gnosis Safe Address)`}</li>
          <li parentName="ul"><strong parentName="li">{`The client's address`}</strong></li>
          <li parentName="ul"><strong parentName="li">{`Total payment amount`}</strong></li>
          <li parentName="ul"><strong parentName="li">{`Number of milestones`}</strong>{` (a typical raid will have 2). Currently, milestones must have uniform payments. Future versions may allow different payment amounts per milestone.`}</li>
          <li parentName="ul"><strong parentName="li">{`Safety valve withdrawal date`}</strong>{`. This is the date past which the client can withdraw any remaining funds in escrow, on the assumption that the raid has not delivered on its promise. Make sure to leave plenty of buffer after the expected end of the raid.`}</li>
        </ul></li>
      <li parentName="ol">{`Save Campaign lead sends the client the raid ID (from Save Central -- Raids V2) or the url to the raid escrow.`}</li>
      <li parentName="ol">{`Client `}<inlineCode parentName="li">{`Deposits`}</inlineCode>{` total raid payment (note: must use the `}<inlineCode parentName="li">{`Deposit`}</inlineCode>{` function).`}</li>
      <li parentName="ol">{`Client `}<inlineCode parentName="li">{`Releases`}</inlineCode>{` first milestone payment`}</li>
      <li parentName="ol">{`Save commences`}</li>
      <li parentName="ol">{`Once next milestone is reached, client releases the next payment`}</li>
      <li parentName="ol">{`Repeat 6 until all milestones have been completed and all money has been released`}</li>
    </ol>
    <blockquote>
      <p parentName="blockquote">{`Note: currently, the escrow contract requires that clients deposit the entire payment amount at once. For larger raids, this may be burdensome for clients. Future versions will allow clients to deposit enough to cover the next milestone and then top up as milestones are finished. For now, this escrow service may be best applied to smaller raids or where clients already have all funds on hand.`}</p>
    </blockquote>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
