  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'lead-a-raid',
	title: 'Lead a Raid',
	sidebar_label: 'Lead a Raid'
};
export const metadata = {
  "id": "lead-a-raid",
  "title": "Lead a Raid",
  "description": "Well well well! Welcome to the big leagues.",
  "source": "@site/docs/lead-a-raid.md",
  "permalink": "/savedao/docs/lead-a-raid",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/lead-a-raid.md",
  "sidebar_label": "Lead a Raid",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Join a Raid",
    "permalink": "/savedao/docs/join-a-raid"
  },
  "next": {
    "title": "Ship a Raid",
    "permalink": "/savedao/docs/shipped"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Well well well! Welcome to the big leagues.`}</p>
    <p>{`Looks like you’ve decided to step your Raiding up a level!`}</p>
    <p>{`As a Leader of a Raid, you will bear the responsibility of seeing it through from start to finish - through the good, the bad and the ugly. As emphasized in our code of conduct we prioritize shipping at all costs. This often means you’ll need to go above and beyond to make sure all gaps are filled.`}</p>
    <p>{`Where leading certain Raids may not have hard coded economic benefits, you do stand to earn a DAO Badge for successfully completing your first Raid. To Lead a Raid, please do the following:`}</p>
    <ol>
      <li parentName="ol">{`Ensure the Raid is logged in Airtable as `}<inlineCode parentName="li">{`Raiding`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`Find a `}<strong parentName="li">{`Cleric`}</strong>{` (Account Manager) and a `}<strong parentName="li">{`Monk`}</strong>{` (Project Manager) responsible for interfacing with the client. If you wish to perform these aspects yourself, please self-assign yourself the role.`}</li>
      <li parentName="ol">{`Signal that you will be leading a Raid either during Round Table or by posting in Guild HQ `}<span class='channels'>{`#general`}</span>{`.`}</li>
      <li parentName="ol">{`Establish a Scope of Work for the Raid. This task is generally handled by Clerics or Monks, but in the case this is you, please ensure a timeline, scope and budget is properly laid out to ensure expectations are aligned. An outline of a Scope of Work can be found `}<a parentName="li" {...{
          "href": "https://github.com/raid-guild/HQ/blob/master/Templates/Proposal.md"
        }}>{`here`}</a>{`.`}</li>
      <li parentName="ol">{`Create a Raid Party using Guild HQ `}<span class='channels'>{`#who-is-available`}</span>{` and tagging the relevant roles like `}<strong parentName="li">{`Archer`}</strong>{` (Design) or `}<strong parentName="li">{`Paladin`}</strong>{` (Back-end)`}</li>
      <li parentName="ol">{`If a Client Chat has not been started, please `}<strong parentName="li">{`start one`}</strong>{` using the project name so others can easily identify the Raid. (i.e. `}<span class='channels'>{`#aave`}</span>{`)`}</li>
      <li parentName="ol">{`Start a `}<span class='channels'>{`#raid`}</span>{` channel and ensure access is restricted only to Raid Guild Members and Apprentices. Please reference the permissions of other Raid Channels for guidance`}</li>
      <li parentName="ol">{`While not mandatory, we strongly recommend setting up a `}<a parentName="li" {...{
          "href": "https://gnosis-safe.io/"
        }}>{`Gnosis Safe Multisig Wallet`}</a>{` for your Raid Party to accept client payment. This ensures that funding can be allocated and distributed with consensus from all participants.`}</li>
      <li parentName="ol">{`We also recommend using the `}<a parentName="li" {...{
          "href": "./escrow"
        }}>{`Raid Guid Escrow service`}</a>{` to facilitate payments from the client.`}</li>
    </ol>
    <p>{`For those looking for guidance on how to structure a Raid bounty, here’s what we suggest:`}</p>
    <ul>
      <li parentName="ul">{`10% War Chest Treasury `}{`—`}{` `}<em parentName="li">{`mandatory`}</em></li>
      <li parentName="ul">{`5% Hunter (Biz Dev)`}</li>
      <li parentName="ul">{`10% Cleric (Account Manager)`}</li>
      <li parentName="ul">{`15% Monk (Project Manager)`}</li>
      <li parentName="ul">{`60% Project (Wizard, Paladin, Archer, etc.)`}</li>
    </ul>
    <p>{`This is meant to be a framework and as such, different Raid breakdowns may vary. All that we ask is that a minimum of 10% of Raid work is tributed back to the War Chest upon completion.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
