export const rightToc = [{
  value: 'Uncategorized',
  id: 'uncategorized',
  children: []
}, {
  value: 'Consultation Fee',
  id: 'consultation-fee',
  children: []
}, {
  value: 'Prepping for Campaign and/or Lost',
  id: 'prepping-for-campaign-andor-lost',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h3 {...{
      "id": "uncategorized"
    }}>{`Uncategorized`}</h3>
    <p>{`All client submissions that have paid for a consultation land into Save Dao's “Raid Central V2 Airtable” and display as `}<inlineCode parentName="p">{`Uncategorized`}</inlineCode>{`.  All project submissions will soon require a deposit to the GnosisSafe for a consultation.`}</p>
    <p>{`Account managers (a.k.a. Clerics) reach out to clients to set up a consultation. Once you've reached out to a client, add that to the notes in the submission. Then people will know that you're the lead on that project.`}</p>
    <h3 {...{
      "id": "consultation-fee"
    }}>{`Consultation Fee`}</h3>
    <p>{`Raid Guild requires a 500 DAI consultation fee be paid by the Client to Raid Guild, this ensures that Clerics (Account Managers) are compensated for their work with Clients before any work commences.`}</p>
    <p>{`The Consultations are designed to be handled by one to two persons max to keep payment per person high enough to be equitable. `}</p>
    <p>{`We recommend that within one month of completing the consultation you submit a `}<inlineCode parentName="p">{`Funding Proposal`}</inlineCode>{` to the `}<a parentName="p" {...{
        "href": "https://app.daohaus.club/dao/0x64/0xfe1084bc16427e5eb7f13fc19bcd4e641f7d571f/proposals/new"
      }}>{`Raid Guild DAO`}</a>{` requesting your fair share of the consultation fee.  After you have received payment, make sure to submit a funding proposal to tribute 10% as spoils back to the Raid Guild War Chest.  `}</p>
    <p>{`If you are a Cleric who chooses not to claim your consultation fee within one month of completing the consultation, be aware that the longer you wait the more you risk losing the opportunity to claim the funds for that consultation all together, that is if the Guild does not recognize your funding proposal.`}</p>
    <h3 {...{
      "id": "prepping-for-campaign-andor-lost"
    }}>{`Prepping for Campaign and/or Lost`}</h3>
    <p>{`After consultation (30-60min) is conducted, if you feel you are not equipped to carry the Raid forward, refer someone else and move the project from `}<inlineCode parentName="p">{`uncategorized`}</inlineCode>{` to the `}<inlineCode parentName="p">{`lost`}</inlineCode>{` category in the Airtable.  Next you will want to pull in appropriate subject matter experts needed to scope the project using the `}<inlineCode parentName="p">{`Commission Process`}</inlineCode>{` (see below section).  `}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
