  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'champion-a-member',
	title: 'Champion a New Member',
	sidebar_label: 'Champion a New Member'
};
export const metadata = {
  "id": "champion-a-member",
  "title": "Champion a New Member",
  "description": "Save Dao grows organically, adding new members as individual existing members deem appropriate and champion them in. Our championing process relies both on the Champion being a good judge of who is a good fit for the Dao as well as on the Champion’s reputation within the Dao being at stake.",
  "source": "@site/docs/champion-a-member.md",
  "permalink": "/savedao/docs/champion-a-member",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/champion-a-member.md",
  "sidebar_label": "Champion a New Member",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Become a Member",
    "permalink": "/savedao/docs/become-a-member"
  },
  "next": {
    "title": "Convert Client Submission Into a SAVE",
    "permalink": "/savedao/docs/convert-client-submission"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '$Save Staking',
  id: 'save-staking',
  children: []
}, {
  value: '$Save Reward',
  id: 'save-reward',
  children: []
}, {
  value: 'Success Criteria',
  id: 'success-criteria',
  children: []
}, {
  value: 'Championing Process',
  id: 'championing-process',
  children: []
}, {
  value: 'References',
  id: 'references',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Save Dao grows organically, adding new members as individual existing members deem appropriate and champion them in. Our championing process relies both on the Champion being a good judge of who is a good fit for the Dao as well as on the Champion’s reputation within the Dao being at stake.`}</p>
    <p>{`In addition to reputation, to ensure that we are only bringing the highests quality new members, Champions must also put down $Save as a semi-financial stake.`}</p>
    <p>{`We also recognize that Championing involves a degree of mentorship. To encourage and compensate Champions for their mentorship of new members, Champions of successful new members will receive a $Save reward in addition to receiving back their original stake.`}</p>
    <h3 {...{
      "id": "save-staking"
    }}>{`$Save Staking`}</h3>
    <p>{`To champion a new member, a Champion must stake $Save. The stake amount depends on the type of applicant:`}</p>
    <ul>
      <li parentName="ul">{`If the applicant has finished one of the Save Dao Cohorts, the required stake is `}<inlineCode parentName="li">{`25,000 $Save`}</inlineCode><ul parentName="li">
          <li parentName="ul">{`Exception: if the Champion is from the same cohort season as the applicant, the required stake is the same as for non-cohort applicants.`}</li>
        </ul></li>
      <li parentName="ul">{`If not, the required stake is `}<inlineCode parentName="li">{`50,000 $Save`}</inlineCode></li>
    </ul>
    <p>{`The Champion will receive their stake back if the new member meets the `}<a parentName="p" {...{
        "href": "./champion-a-member#Success-Criteria"
      }}>{`success criteria`}</a>{` outlined below.`}</p>
    <h3 {...{
      "id": "save-reward"
    }}>{`$Save Reward`}</h3>
    <p>{`Champions receive a reward of `}<inlineCode parentName="p">{`25,000 Save`}</inlineCode>{` if their new member meets `}<a parentName="p" {...{
        "href": "./champion-a-member#Success-Criteria"
      }}>{`success criteria`}</a>{` outlined below.`}</p>
    <h3 {...{
      "id": "success-criteria"
    }}>{`Success Criteria`}</h3>
    <p>{`New members are successful when they contribute to the Save Dao community, bring in value to the Dao, and earn income for themselves.`}</p>
    <p>{`Within the first 60 days after their membership proposal is processed, new members must:`}</p>
    <ol>
      <li parentName="ol">{`Successfully join at least 1 `}<a parentName="li" {...{
          "href": "./join-a-raid"
        }}>{`raid`}</a>{` or `}<a parentName="li" {...{
          "href": "./rips"
        }}>{`RIP`}</a>{`. This include raids or RIPs completed within the first 60 days as well as raids or RIPs still in progress (provided the member is in good standing on that project).`}</li>
      <li parentName="ol">{`Attend at least 2 `}<a parentName="li" {...{
          "href": "./community-meetings"
        }}>{`Dao meetings`}</a>{`, including Roundtable and Raider Rally.`}</li>
      <li parentName="ol">{`Demonstrate shared values with the Dao (at the discretion of the DAO).`}</li>
    </ol>
    <h3 {...{
      "id": "championing-process"
    }}>{`Championing Process`}</h3>
    <p>{`Follow these steps to Champion in a new member:`}</p>
    <ol>
      <li parentName="ol">{`Stake the appropriate amount of $Save by transferring the $Save to the `}<inlineCode parentName="li">{`New Member Championing`}</inlineCode>{` minion at this address: `}<inlineCode parentName="li">{`0x45b49f854eebadbdb59ede30fe5b37ba56a0f26c`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`Send the blockscout transaction url for your stake transaction to the applicant.`}</li>
      <li parentName="ol">{`Have the applicant submit a membership proposal to the DAO and include the stake transaction url in the proposal description.`}</li>
      <li parentName="ol">{`If/once the proposal passes and is processed, help the new member get onboarded and engaged in Dao activities.`}</li>
      <li parentName="ol">{`Record your new member's activity related to the new member `}<a parentName="li" {...{
          "href": "./champion-a-member#Success-Criteria"
        }}>{`success criteria`}</a>{`.`}</li>
      <li parentName="ol">{`If/once your new member has met the new member `}<a parentName="li" {...{
          "href": "./champion-a-member#Success-Criteria"
        }}>{`success criteria`}</a>{`, make a proposal requesting your original stake plus your reward from the `}<inlineCode parentName="li">{`New Member Championing`}</inlineCode>{` minion. Your proposal should include proof of the new member's achievements against the success criteria. This is how the Dao will validate their success.s`}</li>
    </ol>
    <h3 {...{
      "id": "references"
    }}>{`References`}</h3>
    <ul>
      <li parentName="ul">{`The `}<a parentName="li" {...{
          "href": "https://forum.daohaus.club/t/proposal-using-raid-for-new-member-championing-incentives/2031"
        }}>{`original proposal`}</a>{` initializing the Championing Staking program`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
