  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'become-an-apprentice',
	title: 'Become an Apprentice',
	sidebar_label: 'Become an Apprentice'
};
export const metadata = {
  "id": "become-an-apprentice",
  "title": "Become an Apprentice",
  "description": "Save Dao has instituted a new onboarding process through the utilization of Cohorts.",
  "source": "@site/docs/become-an-apprentice.md",
  "permalink": "/savedao/docs/become-an-apprentice",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/become-an-apprentice.md",
  "sidebar_label": "Become an Apprentice",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Roles",
    "permalink": "/savedao/docs/overview-roles"
  },
  "next": {
    "title": "Become a Member",
    "permalink": "/savedao/docs/become-a-member"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Save Dao has instituted a new onboarding process through the utilization of Cohorts. `}</p>
    <p>{`As each cohort evolves, this process will change. In 2022, the below information regarding 1 share tributes is no longer current, but has been left for posterity.  `}</p>
    <p><em parentName="p">{`Note If Season(n) is in session or has not yet begun, we recommend that you find someone with the role @TavernKeeper to vouch for you and get you setup as an apprentice.`}</em></p>
    <p>{`All new entries will start as an Apprentice. Apprentices can earn a maximum of 1 share and may be upgraded to a Guilder by pledging 500 wxDAI as tribute for 100 Shares after being championed. Here’s how it works:`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Submit an application on the Save Dao homepage > `}<a parentName="p" {...{
            "href": "www.zerowasteadvocacy.com"
          }}>{`Join Us`}</a>{`.`}</p>
        <ul parentName="li">
          <li parentName="ul">{`Take your time filling out the Join Us form, as we will determine whether or not you’re a good fit for the Dao based on your ability to effectively communicate your skills and the value you will bring to the Dao as an Apprentice. Also, keep in mind that simply filling out the “Join Us” form does not guarantee you entry into Save Dao as an Apprentice.`}</li>
        </ul>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Join the `}<a parentName="p" {...{
            "href": "https://discord.com/invite/RWjkQ6DNnv"
          }}>{`Save Dao Discord`}</a>{` and introduce yourself in `}<span class='channels'>{`#tavern`}</span>{`. `}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Showcase your past work and skills in `}<span class='channels'>{`#shill-your-stuff`}</span>{`.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Look out for issues to work on in `}<span class='channels'>{`#projects`}</span>{` to earn reputation.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Once you prove yourself with the skills and reputation, a member of the Dao will signal your eligibility to receive your 1 Apprentice Share.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Submit a Membership Proposal for 1 Share with 0 Token Tribute in `}<a parentName="p" {...{
            "href": "https://app.daohaus.club/dao/0x64/0xfe1084bc16427e5eb7f13fc19bcd4e641f7d571f"
          }}>{`Save Dao Guild`}</a>{`.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Once the proposal passes, you will be given access to member exclusive channels.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`For the remaining 99 shares to get yourself promoted from an Apprentice to Member, you will need to have worked on one Save or RIP (See How to Join a Save) and pledge with funds you earn while raiding or have at your disposal through other means.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`A Member will oversee your progress and once it's done, you will be championed in `}<span class='channels'>{`#champions`}</span>{` as a signal for your remaining 99 share proposal.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Submit a Member Proposal for 99 Shares with 495 wxDAI Tribute in `}<a parentName="p" {...{
            "href": "https://app.daohaus.club/dao/0x64/0xfe1084bc16427e5eb7f13fc19bcd4e641f7d571f"
          }}>{`Save Dao DAO`}</a>{`.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Once the proposal passes, you are now a full Save Dao Member.`}</p>
      </li>
    </ol>
    <p><em parentName="p">{`Note: In some cases, Members may earn shares for past work instead of pledging tribute. However, each Member is expected to contribute 500wxDAI (or 500 wxdDAI worth of value) to the DAO in order to become a Member.`}</em></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
