  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'client-instructions',
	title: 'Instructions for Client Payment',
	sidebar_label: 'Instructions for Client Payment'
};
export const metadata = {
  "id": "client-instructions",
  "title": "Instructions for Client Payment",
  "description": "The Save party lead is responsible for working with the client to help them make their deposit into the escrow. The following deposit instructions can be shared with clients:",
  "source": "@site/docs/client-instructions.md",
  "permalink": "/savedao/docs/client-instructions",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/client-instructions.md",
  "sidebar_label": "Instructions for Client Payment",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Interacting With the Escrow",
    "permalink": "/savedao/docs/interacting-with-escrow"
  },
  "next": {
    "title": "References",
    "permalink": "/savedao/docs/references"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Paying with an Externally Owned Account (e.g. MetaMask)',
  id: 'paying-with-an-externally-owned-account-eg-metamask',
  children: []
}, {
  value: 'Paying with a Gnosis Safe',
  id: 'paying-with-a-gnosis-safe',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The Save party lead is responsible for working with the client to help them make their deposit into the escrow. The following deposit instructions can be shared with clients:`}</p>
    <h2 {...{
      "id": "paying-with-an-externally-owned-account-eg-metamask"
    }}>{`Paying with an Externally Owned Account (e.g. MetaMask)`}</h2>
    <p>{`If you are paying Save Dao from an EOA like MetaMask, follow these instructions:`}</p>
    <ol>
      <li parentName="ol">{`Connect your MetaMask or WalletConnect wallet`}</li>
      <li parentName="ol">{`Follow the instructions to deposit. There will be 2 transactions - the first to approve the Save Dao Escrow contract to transfer your tokens, and the second to make the deposit itself.`}</li>
      <li parentName="ol">{`Refresh to verify that the deposit has been made (you may need to reconnect your wallet).`}</li>
    </ol>
    <h2 {...{
      "id": "paying-with-a-gnosis-safe"
    }}>{`Paying with a Gnosis Safe`}</h2>
    <p>{`If you are paying Save Dao from a Gnosis Safe, follow the below instructions to generate the correct transaction from the Gnosis Safe itself. Alternatively, you can use the WalletConnect app in the Gnosis Safe to interact with the Save Dao Escrow app directly (e.g., as if you were using Metamask).`}</p>
    <h4 {...{
      "id": "step-1-approve-the-save-dao-escrow-contract-to-transfer-your-tokens"
    }}>{`Step 1: Approve the Save Dao Escrow contract to transfer your tokens`}</h4>
    <ol>
      <li parentName="ol">{`Go to your Gnosis Safe interface`}</li>
      <li parentName="ol">{`Click New Transaction`}</li>
      <li parentName="ol">{`Click Contract Interaction`}</li>
      <li parentName="ol">{`In the Recipient field, paste the address of the token you're paying with`}<ul parentName="li">
          <li parentName="ul">{`wxDAI: `}<inlineCode parentName="li">{`0xe91D153E0b41518A2Ce8Dd3D7944Fa863463a97d`}</inlineCode></li>
          <li parentName="ul">{`WETH: `}<inlineCode parentName="li">{`0x36c06Cb8EE68D632bDEfeb969b4751F73c67d891`}</inlineCode></li>
        </ul></li>
      <li parentName="ol">{`The ABI field will auto-populate and a dropdown menu will appear underneath`}</li>
      <li parentName="ol">{`From the dropdown menu, select the Approve function. Two new fields will appear underneath`}</li>
      <li parentName="ol">{`In the first field (`}<inlineCode parentName="li">{`usr`}</inlineCode>{` for DAI or `}<inlineCode parentName="li">{`guy`}</inlineCode>{` for WETH), paste the address of the Save Dao Escrow contract on xDAI: `}<inlineCode parentName="li">{`0x7f8F6E42C169B294A384F5667c303fd8Eedb3CF3`}</inlineCode></li>
      <li parentName="ol">{`In the second field (`}<inlineCode parentName="li">{`wad`}</inlineCode>{`), enter the amount of tokens to deposit, multiplied by 10^18.`}<ul parentName="li">
          <li parentName="ul">{`If your payment does not have any decimals (e.g. 10 DAI, not 10.5 DAI), then multiplying by 10^18 is equivalent to adding 18 zeroes to the end.`}</li>
          <li parentName="ul">{`You can copy/paste 18 zeroes from here: `}<inlineCode parentName="li">{`000000000000000000`}</inlineCode></li>
        </ul></li>
      <li parentName="ol">{`Click Review`}</li>
      <li parentName="ol">{`Check that everything matches the correct inputs, and then click Submit`}</li>
      <li parentName="ol">{`Process the transaction as you would any other Gnosis Safe transaction`}</li>
    </ol>
    <h4 {...{
      "id": "step-2-deposit-into-the-escrow"
    }}>{`Step 2: Deposit into the escrow`}</h4>
    <ol>
      <li parentName="ol">{`Click New Transaction again`}</li>
      <li parentName="ol">{`Click Contract Interaction`}</li>
      <li parentName="ol">{`in the Recipient field, paste the address of the Save Dao Escrow contract: `}<inlineCode parentName="li">{`0x7f8F6E42C169B294A384F5667c303fd8Eedb3CF3`}</inlineCode></li>
      <li parentName="ol">{`The ABI field will auto-populate and a dropdown menu will appear underneath`}</li>
      <li parentName="ol">{`From the dropdown menu, select the confirmLocker function. Two new fields will appear underneath`}</li>
      <li parentName="ol">{`In the first field (`}<inlineCode parentName="li">{`value`}</inlineCode>{`), enter `}<inlineCode parentName="li">{`0`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`In the second field (`}<inlineCode parentName="li">{`index`}</inlineCode>{`). enter `}{`[Escrow Index]`}{` -- `}<em parentName="li">{`[Save party lead: replace `}{`[Escrow Index]`}{` with the Escrow Index from Airtable]`}</em></li>
      <li parentName="ol">{`Click Review`}</li>
      <li parentName="ol">{`Check that everything matches the correct inputs, and then click Submit`}</li>
      <li parentName="ol">{`Process the transaction as you would any other Gnosis Safe transaction`}</li>
    </ol>
    <p>{`Finally, check with Save Dao to confirm that the deposit was successful.`}</p>
    <p>{`Note: Gnosis Safe's can now use WalletConnect to connect to web3 applications directly. If that works, things will be much easier than following all the steps above. Unfortunately, it doesn't always work.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
