  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'become-a-member',
	title: 'Become a Member',
	sidebar_label: 'Become a Member'
};
export const metadata = {
  "id": "become-a-member",
  "title": "Become a Member",
  "description": "To advance from an Apprentice to a Member, you will need to be Championed by an existing Save Dao Member. Once you find a Champion, there are a few Your champion will need to put down a stake of $RAID tokens. See the Champion a New Member for how this works.",
  "source": "@site/docs/become-a-member.md",
  "permalink": "/savedao/docs/become-a-member",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/become-a-member.md",
  "sidebar_label": "Become a Member",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Become an Apprentice",
    "permalink": "/savedao/docs/become-an-apprentice"
  },
  "next": {
    "title": "Champion a New Member",
    "permalink": "/savedao/docs/champion-a-member"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`To advance from an Apprentice to a Member, you will need to be Championed by an existing Save Dao Member. Once you find a Champion, there are a few Your champion will need to put down a stake of $RAID tokens. See the `}<a parentName="p" {...{
        "href": "./champion-a-member"
      }}>{`Champion a New Member`}</a>{` for how this works.`}</p>
    <p>{`Once youâ€™ve found a Champion, you are eligible to level up from an Apprentice to a Member - signalled by 144 Shares as opposed to 1. Here's what to do:`}</p>
    <p>{`Follow the `}<a parentName="p" {...{
        "href": "./proposal-types"
      }}>{`Proposal Process`}</a>{` and use the Membership Proposal to request the difference in Shares to get to your 144 Share cap in the `}<a parentName="p" {...{
        "href": "https://xdai.colony.io/colony/save/0x701ec7674e59E0986146CF53ab2DFc59706ce62C"
      }}>{`Save Dao`}</a>{`. In the description of your proposal, enter a blockscout link to the staking transaction that your Champion gives you. This is how the DAO will validate that your Champion made the appropriate stake.`}</p>
    <div {...{
      "className": "admonition admonition-info alert alert--info"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M7 2.3c3.14 0 5.7 2.56 5.7 5.7s-2.56 5.7-5.7 5.7A5.71 5.71 0 0 1 1.3 8c0-3.14 2.56-5.7 5.7-5.7zM7 1C3.14 1 0 4.14 0 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm1 3H6v5h2V4zm0 6H6v2h2v-2z"
              }}></path></svg></span>{`info`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Please note that all new members are capped at 144 Shares for 500 wxDAI.`}</p></div></div>
    <p>{`Once your proposal has passed and been processed, you'll now have 100 Shares and full membership in Save Dao!`}</p>
    <p>{`To get access to member-exclusive channels in the Save Dao `}<a parentName="p" {...{
        "href": "https://discord.gg/yarBYA9yx9"
      }}>{`Discord server`}</a>{`, head to the `}<span class='channels'>{`#member-join`}</span>{` channel and enter the `}<inlineCode parentName="p">{`!join`}</inlineCode>{` command. Then follow the CollabLand instructions to get your `}<inlineCode parentName="p">{`@Save Dao Member`}</inlineCode>{` role. Now you're ready to `}<a parentName="p" {...{
        "href": "./join-a-save"
      }}>{`join a save`}</a>{`!`}</p>
    <p>{`To ensure that your Champion gets their $RAID stake back, you'll need to fulfill the following `}<a parentName="p" {...{
        "href": "./champion-a-member#Success-Criteria"
      }}>{`3 criteria`}</a>{` `}<strong parentName="p">{`within your first 60 days`}</strong>{` as a member (starting from when your membership proposal is processed):`}</p>
    <ol>
      <li parentName="ol">{`Successfully join at least 1 `}<a parentName="li" {...{
          "href": "./join-a-save"
        }}>{`raid`}</a>{` or `}<a parentName="li" {...{
          "href": "./rips"
        }}>{`RIP`}</a>{`. This include raids or RIPs completed within the first 60 days as well as raids or RIPs still in progress (provided that you are in good standing on that project).`}</li>
      <li parentName="ol">{`Attend at least 2 `}<a parentName="li" {...{
          "href": "./community-meetings"
        }}>{`Dao meetings`}</a>{`, including Roundtable and the "Dao-dreaming" stage in discord.`}</li>
      <li parentName="ol">{`Demonstrate shared values with the Dao (at the discretion of the DAO).`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
