  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'commission-process',
	title: 'Commission Process',
	sidebar_label: 'Commission Process'
};
export const metadata = {
  "id": "commission-process",
  "title": "Commission Process",
  "description": "After the Save enters Prepping for Battle, the Cleric who led the consultation is responsible for gathering additional Saveers as is necessary to scope out the full project.",
  "source": "@site/docs/comission-process.md",
  "permalink": "/savedao/docs/commission-process",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/comission-process.md",
  "sidebar_label": "Commission Process",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Convert Client Submission Into a SAVE",
    "permalink": "/savedao/docs/convert-client-submission"
  },
  "next": {
    "title": "How Does It Work?",
    "permalink": "/savedao/docs/how-does-it-work"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Saveing',
  id: 'saveing',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`After the Save enters Prepping for Battle, the Cleric who led the consultation is responsible for gathering additional Saveers as is necessary to scope out the full project. `}</p>
    <p>{`All Saveers who participate in the `}<inlineCode parentName="p">{`Prepping for Battle`}</inlineCode>{` phase are entitled to compensation, right now that is 3% of the project budget that will be allocated as a commission to the Saveers involved.`}</p>
    <p>{`This commission comes out of the client payment for the project itself, keep in mind this is separate from the consultation fee.`}</p>
    <p>{`For example, let’s say a client submits a consultation for a 40k DAI project.  First, a Cleric would take the consultation and earn the consultation fee.  During the consultation, it was decided the project budget needed to be increased to 50k DAI to accommodate for additional scope.  At this point, the Saveer would want to pull in subject matter experts i.e 1 designer and 2 engineers to help scope the project.  A commission of 1,500 DAI or 3% would then be owed to those who participated.  `}</p>
    <p>{`Following completion of the commission process, send your client a Save Proposal that defines the scope of work to be completed by the Save Party.`}</p>
    <h3 {...{
      "id": "saveing"
    }}>{`Saveing`}</h3>
    <p>{`Once the client has agreed to the project `}<strong parentName="p">{`and sent the first half of the project payment`}</strong>{` then move the project to `}<inlineCode parentName="p">{`Saveing`}</inlineCode>{`.`}</p>
    <p>{`After the project has moved from `}<inlineCode parentName="p">{`Preparing for Campaign`}</inlineCode>{` → `}<inlineCode parentName="p">{`Saveing`}</inlineCode>{`, the team would be responsible for collecting their commission payment from a shared multisig or DAO they set up.`}</p>
    <p>{`Save Guild introduced this new commission process to further incentivize Saveers to close deals and compensate Saveers for their time leading up to Saveing. For more details on the commission process, see the `}<a parentName="p" {...{
        "href": "https://docs.google.com/spreadsheets/d/1hy9D4f_nX79d9MXmZQlCZiAnSDViJOVugzLVYb7BFuk/edit?usp=sharing"
      }}>{`Commission Model`}</a>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
