  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'migration-to-xdai',
	title: 'Migration to xDAI',
	sidebar_label: 'Migration to xDAI'
};
export const metadata = {
  "id": "migration-to-xdai",
  "title": "Migration to xDAI",
  "description": "On February 18, 2021, Raid Guild migrated its on-chain DAO to the xDAI network in order to reduce the burden of high gas fees on Ethereum mainnet and facilitate more on-chain interactions, governance, and experimentation.",
  "source": "@site/docs/migration-to-xdai.md",
  "permalink": "/savedao/docs/migration-to-xdai",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/migration-to-xdai.md",
  "sidebar_label": "Migration to xDAI",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Getting Paid",
    "permalink": "/savedao/docs/requesting-payment"
  },
  "next": {
    "title": "How Tos",
    "permalink": "/savedao/docs/helping-docs"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Share Price Change',
  id: 'share-price-change',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`On February 18, 2021, Raid Guild migrated its on-chain DAO to the xDAI network in order to reduce the burden of high gas fees on Ethereum mainnet and facilitate more on-chain interactions, governance, and experimentation.`}</p>
    <p>{`Our migration `}<a parentName="p" {...{
        "href": "https://forum.daohaus.club/t/how-to-do-the-xdai-migration/278"
      }}>{`proceeded`}</a>{` as follows:`}</p>
    <ol>
      <li parentName="ol">{`Create a Gnosis Safe multisig with 7 active Raid Guild members on mainnet, and an equivalent Safe on xDAI`}</li>
      <li parentName="ol">{`Initiate proposals to send the balance of each token in the War Chest to the mainnet Safe`}</li>
      <li parentName="ol">{`Vote on, pass, and process the proposals, resulting in the tokens being sent to the mainnet Safe`}</li>
      <li parentName="ol">{`From the Safe, send each token over the `}<a parentName="li" {...{
          "href": "https://dai-bridge.poa.network/"
        }}>{`xDAI`}</a>{` `}<a parentName="li" {...{
          "href": "https://xdai-omnibridge.web.app/"
        }}>{`bridge`}</a>{` to the Safe on xDAI`}</li>
      <li parentName="ol">{`Wrap the resulting xDAI into wxDAI`}</li>
      <li parentName="ol">{`Summon a new DAO on xDAI using DAOhaus' multisummoner feature to instantiate the new DAO with all existing members (see the next section for details)`}</li>
      <li parentName="ol">{`From the xDAI Safe, send the balance of each token into the new DAO`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`Sync`}</inlineCode>{` each token in the new DAO bank to reflect the new balances`}</li>
    </ol>
    <h3 {...{
      "id": "share-price-change"
    }}>{`Share Price Change`}</h3>
    <p>{`At the same time as the migration to xDAI, Raid Guild `}<a parentName="p" {...{
        "href": "https://forum.daohaus.club/t/changing-our-share-price-from-an-eth-peg-to-a-dai-peg/230"
      }}>{`changed its membership and share price model`}</a>{`. This was done in order to return the cost of joining the guild to a more reasonable rate -- $500 vs. 1 ETH ($1900 at the time) -- and to ensure that it stays that way in the future.`}</p>
    <p>{`However, this change created a new wrinkle. Setting the new cost of joining the guild so much lower than previously would have diluted the financial value of existing shares as new members joined. To counteract this effect, Raid Guild granted additional shares to existing members at the cutover time. The amount of additional shares was determined by the ratio of the previous joining price (1 WETH = $1900) to the new joining price ($500), i.e. `}<inlineCode parentName="p">{`3.8`}</inlineCode>{`.`}</p>
    <p>{`Since Moloch shares are integers, introducing this decimal multiplier would have caused some rounding errors, so Raid Guild split `}<em parentName="p">{`all`}</em>{` shares (both pre- and post-cutover) by a factor of 10. This meant that existing members' shares were multiplied by `}<inlineCode parentName="p">{`38`}</inlineCode>{` in the new xDAI DAO, the new minimum shares for full membership increased from 10 to 100 shares, and the new share price is now $5 per share. The full calculation can be found in `}<a parentName="p" {...{
        "href": "https://docs.google.com/spreadsheets/d/1VTY0Qr7blE9fN1b76PT0Hi_F8bCEHIbLBi1xZon7oBc/edit#gid=0"
      }}>{`this worksheet`}</a>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
