  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'glossary',
	title: 'Glossary',
	sidebar_label: 'Glossary'
};
export const metadata = {
  "id": "glossary",
  "title": "Glossary",
  "description": "A running list of Raid Guild nomenclature. Always evolving, forever in progress…",
  "source": "@site/docs/glossary.md",
  "permalink": "/savedao/docs/glossary",
  "editUrl": "https://github.com/raid-guild/handbook/edit/master/docs/glossary.md",
  "sidebar_label": "Glossary",
  "sidebar": "howSidebar",
  "previous": {
    "title": "Learn About Web3",
    "permalink": "/savedao/docs/learn-about-web3"
  },
  "next": {
    "title": "Getting Paid",
    "permalink": "/savedao/docs/requesting-payment"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`A running list of Raid Guild nomenclature. Always evolving, forever in progress…`}</p>
    <h4 {...{
      "id": "async"
    }}>{`Async`}</h4>
    <p>{`A conversation to be continued offline following the current discussion. “We can async on that”`}</p>
    <h4 {...{
      "id": "guildkick"
    }}>{`GuildKick`}</h4>
    <p>{`A proposal type to remove members from the DAO. If a GuildKick proposal is successful, the kicked address automatically RageQuits their Shares at the Share Value.`}</p>
    <h4 {...{
      "id": "loot"
    }}>{`Loot`}</h4>
    <p>{`Non-voting Shares in the DAO. Guilders can ragequit Loot to withdraw proportional amount of assets from the DAO based on current Share Value.`}</p>
    <h4 {...{
      "id": "minion"
    }}>{`Minion`}</h4>
    <p>{`A smart contract account which can call other smart contracts on behalf of the DAO.`}</p>
    <h4 {...{
      "id": "ptokens"
    }}>{`pTokens`}</h4>
    <p>{`Personal Tokens. These are tokens which are unique to specific Raiders which can be used to Summon them for a given Raid or feedback on a Proposal. Specific pTokens are Whitelisted by the DAO and stored in the War Chest.`}</p>
    <h4 {...{
      "id": "proposal"
    }}>{`Proposal`}</h4>
    <p>{`A means of requesting something (shares, funding, token whitelists, etc.) from the DAO. All Proposals are made onchain and voted on using Shares by Guilders. Proposals must have more Yes than No votes to pass.`}</p>
    <h4 {...{
      "id": "ragequit"
    }}>{`RageQuit`}</h4>
    <p>{`The act of exiting shares from the DAO in exchange for a pro-rata share of the underlying capital bank relative to Share Value.`}</p>
    <h4 {...{
      "id": "raid"
    }}>{`Raid`}</h4>
    <p>{`A project. Raids can be internal or external, such as improving internal operations or building a public good.`}</p>
    <h4 {...{
      "id": "raid-party"
    }}>{`Raid Party`}</h4>
    <p>{`A team or collective of Guilders working together on a Raid.`}</p>
    <h4 {...{
      "id": "round-table"
    }}>{`Round Table`}</h4>
    <p>{`Weekly call for Members of the Guild to catchup and discuss goings-on in the realm.`}</p>
    <h4 {...{
      "id": "shares"
    }}>{`Shares`}</h4>
    <p>{`Voting Shares in the DAO. Members can ragequit Shares to withdraw a proportional amount of assets from the DAO based on current Share Value.`}</p>
    <h4 {...{
      "id": "share-value"
    }}>{`Share Value`}</h4>
    <p>{`The value of 1 share at any given time. In the case of Raid Guild, Share Value is denominated in wxDAI and can be determined by dividing the outstanding amount of shares by the total amount of WETH (and all other Whitelisted Tokens) in the War Chest.`}</p>
    <h4 {...{
      "id": "spoils"
    }}>{`Spoils`}</h4>
    <p>{`A portion of funds paid out to a Raid Party (10%) that are required to be tributed back to the Raid Guild War Chest`}</p>
    <h4 {...{
      "id": "tavern"
    }}>{`Tavern`}</h4>
    <p>{`A public meeting place for newcomers and veteran adventurers. The tavern is open to all and typically serves as the entry point into the community.`}</p>
    <p>{`Raid Guild contains a Front Tavern for inbound and outbound leads and a Back Tavern to gain technical insight and scope out potential Raids.`}</p>
    <h4 {...{
      "id": "war-chest"
    }}>{`War Chest`}</h4>
    <p>{`The name of Raid Guild’s DAO smart contract. The War Chest stores all funds for the Guild and can only be distributed if a vote passes with consensus from the DAO.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
